import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-past-work-section',
  standalone: true,
  imports: [CommonModule],
  template: `
    <section class="bg-yellow-700 h-[75vh]">
      <div class="hero-section__content ">
        <h1 class="hero-section__title">social credit</h1>
        <p class="hero-section__subtitle">We build beautiful websites</p>
      </div>
    </section>
  `,
    styles: [
      `
      .sticky {
        position: sticky;
        top: 60vh;
        z-index: 100;
      }
      `
    ]
  })
export class PastWorkSectionComponent {

}
