import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ShowcaseContainerComponent } from "../showcase-container/showcase-container.component";
import { VertNavbarComponent } from "../vert-navbar/vert-navbar.component";

@Component({
    selector: 'app-showcase-page',
    standalone: true,
    template: `

<app-vert-navbar></app-vert-navbar>
<div class="flex overflow-x-auto px-20">
  <!-- create a div as a spacer -->
  <app-showcase-container
  [backgroundColor]="'#FDE68A'"
  [routeLink]="'/showcase'"
  [buttonText]="'Learn More'"
  [image]="'https://images.unsplash.com/photo-1612837017953-0e3b5a2b0b0f?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8YmF1aG91cyUyMGJhbm5lcnxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&w=1000&q=80'"
  [num]="'01'"
  [date]="'jan 26, 2022'"
  [text]="'Reimagining the future of work'"
  [subtext]="'Look ar this thing we did with this thing'"
  [ctatext]="'Learn More'"
></app-showcase-container>


</div>
  `,
    styles: [],
    imports: [CommonModule, ShowcaseContainerComponent, VertNavbarComponent]
})
export class ShowcasePageComponent {

}
