import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-showcase-container',
  standalone: true,
  imports: [CommonModule],
  template: `

  <section [style.background-color]="backgroundColor" class="h-screen w-80 ">
  <p>Adaptive Multi-Platform Flutter</p>
  <p>Working with Google, we built apps to showcase Flutter's support for adaptive applications that run on multiple platforms.</p>
</section>
  `,
  styles: [
  ]
})
export class ShowcaseContainerComponent {
  @Input()
  backgroundColor!: string;

}
