import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HoverBtnComponent } from "../hover-btn/hover-btn.component";

@Component({
    selector: 'app-careers-section',
    standalone: true,
    template: `
  <section class="bg-blue-700 h-[75vh]">
    <div class="hero-section__content pl-[5rem]" [ngClass]="{ sticky: isSticky }">
      <div class="h-[10vh]"></div>
      <h1 class="hero-section__title">social credit</h1>
      <p class="hero-section__subtitle">We build beautiful websites</p>
      <app-hover-btn routeLink="/showcase" buttonText="Bauhaus Careers"></app-hover-btn>
      <div class="h-[10vh]"></div>
    </div>
  </section>
`,
    styles: [
        `
    .sticky {
      position: sticky;
      top: 40vh;
      z-index: 100;
    }
    `
    ],
    imports: [CommonModule, HoverBtnComponent]
})
export class CareersSectionComponent {
  isSticky: boolean = true;

  constructor() {
    this.checkSticky();
  }

  checkSticky() {
    window.addEventListener('scroll', () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;
      this.isSticky = scrollTop > 75;
    });
  }
}
