import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HoverBtnComponent } from "../hover-btn/hover-btn.component";

@Component({
    selector: 'app-showcase-container',
    standalone: true,
    template: `
  <section [style.background-color]="backgroundColor" class="w-96 h-screen flex justify-left items-center pt-10">
    <div>
    <img src="src/assets/images/bauhaus_research_claymorphism_image.PNG" alt="Description of the image">
    <p>{{num}}</p>
    <p>{{date}}</p>
    <p>{{text}}</p>
    <p>{{subtext}}</p>
    <app-hover-btn [buttonText]="ctatext"
    ></app-hover-btn>
      </div>
</section>
  `,
    styles: [],
    imports: [CommonModule, HoverBtnComponent]
})
export class ShowcaseContainerComponent {
  @Input()
  backgroundColor!: string;
  @Input()
  routeLink?: string;
  @Input()
  buttonText!: string;
  @Input()
  email?: string;
  @Input()
  num: string = "01";
  @Input()
  date: string = "jan 26, 2022";
  @Input()
  text: string = "Reimagining the future of work";
  @Input()
  subtext: string = "Look ar this thing we did with this thing";
  @Input() ctatext: string = "Learn More";
}
