import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-social-credit-section',
  standalone: true,
  imports: [CommonModule],
  template: `
    <section class="bg-red h-[75vh]">
      <div class="hero-section__content" [ngClass]="{ sticky: isSticky }">
        <h1 class="hero-section__title">social credit</h1>
        <p class="hero-section__subtitle">We build beautiful websites</p>
      </div>
    </section>
  `,
  styles: [
    `
    .sticky {
      position: sticky;
      top: 0;
      z-index: 100;
    }
    `
  ]
})
export class SocialCreditSectionComponent implements OnInit {
  @ViewChild('stickyElement', { static: true }) stickyElementRef!: ElementRef<HTMLDivElement>;
  isSticky: boolean = false;
  containerTop: number = 0;
  containerHeight: number = 0;

  ngOnInit() {
    this.checkSticky();
  }

  checkSticky() {
    window.addEventListener('scroll', () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;
      if (this.stickyElementRef && this.stickyElementRef.nativeElement) {
        const containerElement = this.stickyElementRef.nativeElement.parentElement;
        const containerRect = containerElement.getBoundingClientRect();
        this.containerTop = containerRect.top + window.pageYOffset;
        this.containerHeight = containerRect.height;
        this.isSticky = scrollTop >= this.containerTop && scrollTop <= (this.containerTop + this.containerHeight);
      }
    });
  }
}
