import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-logo-btn',
  standalone: true,
  imports: [CommonModule],
  template: `<div class="fixed top-0 left-0 p-10 grid grid-cols-2">
  <div class="h-10 w-10 bg-cyan-600"></div>
  <div id="logo" class="h-10 w-10 bg-cyan-600"></div>
</div>


  `,
  styles: [`
    #logo {
      opacity: 1;
      transition: opacity 0.5s;
    }

    .hidden {
      opacity: 0;
    }
    `
  ]
})
export class LogoBtnComponent implements OnInit {
  ngOnInit() {
    const box = document.getElementById('logo');

    if(box)
    window.addEventListener('scroll', function () {
      const scrollPosition = window.scrollY;
      const windowHeight = window.innerHeight;
      const boxPosition = box.offsetTop;

      if (scrollPosition > boxPosition - windowHeight) {
        box.classList.remove('hidden');
      } else {
        box.classList.add('hidden');
      }
    });
  }
}
