import { Component, Input } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-showcase-container',
  standalone: true,
  imports: [CommonModule],
  template: `
  <section [style.background-color]="backgroundColor" class="h-screen w-96 flex justify-left items-center">
    <div>
    <p>{{num}}</p>
    <p>{{subtext}}</p>
    <p>{{text}}</p>
    <p>{{subtext}}</p>
      </div>

</section>
  `,
  styles: [
  ]
})
export class ShowcaseContainerComponent {
  @Input()
  backgroundColor!: string;
  num: string = "Hello World";
  date: string = "This is a subtext";
  text: string = "Hello World";
  subtext: string = "This is a subtext";
}
