import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ShowcaseContainerComponent } from "../showcase-container/showcase-container.component";
import { VertNavbarComponent } from "../vert-navbar/vert-navbar.component";

@Component({
    selector: 'app-showcase-page',
    standalone: true,
    template: `

<app-vert-navbar></app-vert-navbar>
<div class="flex overflow-x-auto px-20">
  <!-- create a div as a spacer -->
  <app-showcase-container 
  [backgroundColor]="'#F2F2F2'" [num]="'02'" [text]="'See our work on the dart_statistics package'" [date]="''" [subtext]="''" [routeLink]="'/home'" [buttonText]="'View Docs'" image="src/assets/images/bauhaus_research_claymorphism_image.PNG"></app-showcase-container>
  <app-showcase-container
  [backgroundColor]="'#F2F2F2'" [num]="'02'" [text]="'see our work on the Claymorphism package'" [date]="''" [subtext]="''" [routeLink]="'/home'" [buttonText]="'Clay Playground'" image="src/assets/images/bauhaus_research_claymorphism_image.PNG"></app-showcase-container>
  <app-showcase-container [backgroundColor]="'#F2F2F2'"></app-showcase-container>
  <app-showcase-container [backgroundColor]="'#0000F2'"></app-showcase-container>
  <app-showcase-container [backgroundColor]="'#F2F2F2'"></app-showcase-container>
  <app-showcase-container [backgroundColor]="'#0000F2'"></app-showcase-container>
  <app-showcase-container></app-showcase-container>
  <app-showcase-container></app-showcase-container>
</div>
  `,
    styles: [],
    imports: [CommonModule, ShowcaseContainerComponent, VertNavbarComponent]
})
export class ShowcasePageComponent {

}
