import { Component, ElementRef, HostListener, OnInit, ViewChild } from '@angular/core';

@Component({
  selector: 'app-logo-btn',
  standalone:true,
  template: `
  <div #boxElement class="absolute top-0 left-0 p-10 grid grid-cols-2" [class.hidden]="!isBoxVisible">
    <div class="h-10 w-10 bg-cyan-600"></div>
    <div id="logo" class="h-10 w-10 bg-cyan-600"></div>
  </div>
`,
  styles: [`
    #logo {
      opacity: 1;
      transition: opacity 0.5s;
    }

    .hidden {
      opacity: 0;
      transform: translateY(-10px);
    }

    @keyframes fade-in {
      from {
        opacity: 0;
        transform: translateY(-10px);
      }
      to {
        opacity: 1;
        transform: translateY(0);
      }
    }
  `]
})
export class LogoBtnComponent implements OnInit {
  @ViewChild('boxElement', { static: true }) boxElementRef!: ElementRef<HTMLDivElement>;
  isBoxVisible: boolean = false;
  intersectionObserver: IntersectionObserver | undefined;

  ngOnInit() {
    this.intersectionObserver = new IntersectionObserver(
      (entries) => this.handleIntersection(entries),
      { root: null, rootMargin: '0px', threshold: 0 }
    );

    this.intersectionObserver.observe(this.boxElementRef.nativeElement);
  }

  handleIntersection(entries: IntersectionObserverEntry[]) {
    const isVisible = entries[0].isIntersecting;
    this.isBoxVisible = isVisible;
  }
}
