import { Component, HostListener, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-logo-btn',
  standalone: true,
  imports: [CommonModule],
  template: `
  <div class="fixed top-0 left-0 p-10 grid grid-cols-2">
  <div class="h-10 w-10 bg-cyan-600"></div>
  <div id="logo" class="h-10 w-10 bg-cyan-600"></div>
</div>

<div class="relative top-0 h-10 w-10 bg-cyan-600"></div>
  `,
  styles: [`
    #logo {
      opacity: 1;
      transition: opacity 0.5s;
    }

    .hidden {
      opacity: 0;
      transition: all 1s;
    }

    .show {
      opacity: 1;
    }
    `
  ]
})
export class LogoBtnComponent implements OnInit {
  isBoxVisible: boolean = false;

  ngOnInit() {
    this.toggleBoxVisibility();
  }

  @HostListener('window:scroll')
  toggleBoxVisibility() {
    const box = document.getElementById('logo');
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        console.log(entry)
        if (entry.isIntersecting) {
          entry.target.classList.add('show');
        } else {
          entry.target.classList.remove('show');
        }
      });

    })
    if (box) {
      const scrollPosition = window.scrollY;
      const boxPosition = box.offsetTop;
      this.isBoxVisible = scrollPosition <= boxPosition;
    }
  }
}
