import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-social-credit-section',
  standalone: true,
  imports: [CommonModule],
  template: `
    <section class="bg-red-700 h-[75vh]">
      <div class="hero-section__content" [ngClass]="{ sticky: isSticky }">
        <div class="h-[10vh]"></div>
        <h1 class="hero-section__title">social credit</h1>
        <p class="hero-section__subtitle">We build beautiful websites</p>
        <div class="h-[10vh]"></div>
      </div>
    </section>
  `,
    styles: [
      `
      .sticky {
        position: sticky;
        top: 50vh;
        left: 5rem;
        z-index: 0;
      }
      `
    ]
  })
  export class SocialCreditSectionComponent {
    isSticky: boolean = true;

    constructor() {
      this.checkSticky();
    }

    checkSticky() {
      window.addEventListener('scroll', () => {
        const scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;
        this.isSticky = scrollTop > 75;
      });
    }
  }
